<style scoped lang="scss">
@use 'assets/style/utility';
@use 'assets/style/breakpoints';

.section-hero {
	position: relative;
	overflow: hidden;
	height: max(calc(100vh), #{utility.rem(1000)});
	margin-bottom: #{utility.rem(-96)};
	padding-top: #{utility.rem(196)};
	padding-bottom: #{utility.rem(254)};
	background: #1d2939;
	transform: translateY(#{utility.rem(-96)});
	background-blend-mode: darken;

	:deep(.section-title) {
		position: relative;
		z-index: 1;
		margin-top: #{utility.rem(0)};
		margin-bottom: #{utility.rem(62)};
		color: #fff;
	}

	& &__pattern {
		position: absolute;
		top: unset;
		left: 50%;
		display: block;
		pointer-events: none;
		transform: translateX(-50%);

		svg {
			display: block;
		}

		svg:not(:is(.up-xxl, .up-xl)) {
			width: #{utility.rem(420)};
			height: #{utility.rem(268)};
		}

		> svg:is(.up-xxl, .up-xl) {
			display: none;
		}

		@include breakpoints.media-down('xl') {
			bottom: #{utility.rem(-4)};
		}

		@include breakpoints.media-up('xl') {
			top: 0;

			svg:is(.up-xl) {
				display: block;
			}

			svg:not(.up-xl) {
				display: none;
			}
		}

		@include breakpoints.media-up('xxl') {
			top: 0;

			svg:is(.up-xxl) {
				display: block;
			}

			svg:not(.up-xxl) {
				display: none;
			}
		}

		//@include breakpoints.media-down('xxl') {
		//  svg.xl {
		//    display: block;
		//  }
		//}
	}

	@include breakpoints.media-down('xxl') {
		height: max(calc(100vh - #{utility.rem(96)}), #{utility.rem(840)});
		padding-top: #{utility.rem(146)};
		padding-bottom: #{utility.rem(254)};

		:deep(.section-title) {
			margin-bottom: #{utility.rem(42)};
		}
	}

	@include breakpoints.media-down('xl') {
		height: auto;
		margin-bottom: #{utility.rem(-76)};
		padding-top: #{utility.rem(102)};
		padding-bottom: #{utility.rem(234)};
		transform: translateY(#{utility.rem(-76)});

		:deep(.section-title) {
			margin-bottom: #{utility.rem(24)};
		}
	}
}

